mapper.select:
SELECT DISTINCT p.id,
                p.start_time time,
                p.status


FROM performances p
         JOIN events e ON p.event_id = e.id
         JOIN venues v ON p.venue_id = v.id
         JOIN city c ON c.id = v.city_id
WHERE c.id = #{cityId}
  AND e.id = #{id}
ORDER BY p.start_time;
